<?phpnamespace Application\Form;use Laminas\Form\Form;class AlbumForm extends Form{    public function init()    {        $this->add(          [            'name' => 'album',            'type' => AlbumFieldset::class,            'options' => [              'use_as_base_fieldset' => true,            ],          ]        );        $this->add([            'name' => 'submit',            'type' => 'submit',            'attributes' => [              'value' => 'Go',              'id' => 'Insert new Album',            ],          ]        );    }}